mapper.select:
SELECT rn, BOARD_SEQ, EMAIL, TITLE,HOS_NAME, CONTENT, REGDATE, REFER, READCOUNT, DELFLAG FROM (SELECT ROW_NUMBER() OVER(ORDER BY refer DESC) rn, board_seq, email, title,HOS_NAME, content, regdate, refer, readcount, delflag FROM BOARD WHERE DELFLAG = 'N') a
AND board_seq=#{board_seq}
AND CEIL(rn/10)=#{pnum}
ORDER BY BOARD_SEQ DESC

mapper.update:
UPDATE BOARD
SET readcount=readcount+1
WHERE board_seq=#{board_seq}

mapper.select:
SELECT BOARD_SEQ, EMAIL,HOS_NAME ,TITLE, CONTENT, REGDATE
FROM BOARD 
WHERE BOARD_SEQ = #{board_seq}

mapper.update:
UPDATE BOARD
SET TITLE = #{title}, CONTENT = #{content}
WHERE BOARD_SEQ = #{board_seq}
