mapper.select:
SELECT EMAIL, NAME, ADDRESS, BIRTH, PHONE
FROM USER
WHERE EMAIL = #{email}

mapper.update:
UPDATE USER
SET EMAIL = #{email},  NAME = #{name}, ADDRESS = #{address}, BIRTH = #{birth}, PHONE = #{phone}
WHERE EMAIL = #{email}

mapper.select:
SELECT YADMNM, MDATE, usemoney
FROM RESERVEBOARD
WHERE EMAIL = #{email}

SELECT sum(money)
FROM useraccount
WHERE USERSEQNO = #{userseqno}

SELECT FINTECH_USE_NUM, MONEY, BANK_NAME, USERSEQNO
FROM USERACCOUNT
WHERE USERSEQNO = #{userseqno}
